Here is an proposed way to improve that path:
```cpp
static int __Pyx_ParseOptionalKeywords(
    PyObject *kwds,
    PyObject *const *kwvalues,
    PyObject **argnames[],
    PyObject *kwds2,
    PyObject *values[],
    Py_ssize_t num_pos_args,
    const char* function_name)
{
    PyObject *key = 0, *value = 0;
    Py_ssize_t pos = 0;
    PyObject*** name;
    PyObject*** first_kw_arg = argnames + num_pos_args;
    int kwds_is_tuple = CYTHON_METH_FASTCALL && likely(PyTuple_Check(kwds));

    if (!kwds_is_tuple && kwds && kwds2) {
        // Check dict is unicode
        if (!PyArg_ValidateKeywordArguments(kwds)) goto bad; // Python already sets the error
        // Check for duplications
        name = argnames;
        while (*name && name != first_kw_arg) {
            int check = PyDict_Contains(kwds, **name);
            if (check < 0 && unlikely(PyErr_Occurred())) goto bad;
            if (check == 1) goto arg_passed_twice;
            name++;
        }
        // Fast copy
        if (PyDict_Update(kwds2, kwds) < 0)
           goto bad;
        // We can keep the copy as is
        if (!*first_kw_arg)
            return 0;
        // Retrieve items and remove them
        name = first_kw_arg;
        while (*name) {
            int found;
            value = NULL;
#if CYTHON_COMPILING_IN_CPYTHON && PY_VERSION_HEX >= 0x030d00A2 || defined(PyDict_Pop)
            found = PyDict_Pop(kwds2, **name, &value);
            if (unlikely(found < 0  && PyErr_Occurred())) goto bad;
            if (found) {
                values[name-argnames] = value;
            }
#else
            found = PyDict_GetItemRef(kwds2, **name, &value);
            if (unlikely(found < 0 && PyErr_Occurred())) goto bad;
            if (found == 1) {
                values[name-argnames] = value;
                if (PyDict_DelItem(kwds2, **name) < 0) goto bad;
            }
#endif
            name++;
        }
        return 0;
    }

    while (1) {
        // clean up key and value when the loop is "continued"
        Py_XDECREF(key); key = NULL;
        Py_XDECREF(value); value = NULL;
        [...]
