  #if CYTHON_COMPILING_IN_CPYTHON && PY_VERSION_HEX >= 0x030a0000  ||  defined(Py_XNewRef)
    return Py_XNewRef(obj);
  #else
    Py_XINCREF(obj);
    return obj;
  #endif
}

