    PyObject *func_qualname;
    __Pyx_BEGIN_CRITICAL_SECTION(op);
    func_qualname = op->func_qualname;
    __Pyx_END_CRITICAL_SECTION();
    return PyUnicode_FromFormat("<cyfunction %U at %p>",
                                func_qualname, (void *)op);
